declare module '*.html' {
    const content: string;
    export default content;
  }
  declare module '*.json' {
    const content: Array<any>;
    export default content;
  }
  declare module '*.scss' {
    const content: any;
    export default content;
  }
  declare module '*.css' {
    const content: any;
    export default content;
  }
  declare module '*.js' {
    const content: any;
    export default content;
  }
